import { Link } from "react-router-dom";
import React, { useEffect } from "react";
import Container from "./Container";
import Grid from "./Grid";
import styles from "./Nav.module.css";
import classNames from "classnames";
import "../components/webMenu.css";

function Nav({ onClose }) {
  const handleClose = () => {
    onClose?.();
  };

  useEffect(() => {
    document.body.style.cssText = `
      position: fixed; 
      top: -${window.scrollY}px;
      overflow-y: scroll;
      width: 100%;`;
    return () => {
      const scrollY = document.body.style.top;
      document.body.style.cssText = "";
      window.scrollTo(0, parseInt(scrollY || "0", 10) * -1);
    };
  }, []);

  return (
    <div className={styles.nav}>
      <Container>
        <Grid className={styles.container}>
          <Link to="/">
            <div className={classNames(styles.logo, "title24x")}>JJUN Creative</div>
          </Link>
          <ul className={styles.menu}>
            <Link to="/brand">
              <li>Brand</li>
            </Link>
            <Link to="/foundations">
              <li>Foundations</li>
            </Link>
            <Link to="/components">
              <li>Components</li>
            </Link>
            <Link to="/contents">
              <li>Contents</li>
            </Link>
            <Link to="/resources">
              <li>Resources</li>
            </Link>
          </ul>

          <div className={"menuSize"}>
            <input onClick={handleClose} type="checkbox" id="check_box" />
            <label for="check_box">
              <span></span>
              <span></span>
              <span></span>
            </label>
            <div id="sideMenu">
              <ul>
                <Link to="/brand">
                  <li>Brand</li>
                </Link>
                <Link to="/foundations">
                  <li>Foundations</li>
                </Link>
                <Link to="/components">
                  <li>Components</li>
                </Link>
                <Link to="/contents">
                  <li>Contents</li>
                </Link>
                <Link to="/resources">
                  <li>Resources</li>
                </Link>
              </ul>
            </div>
          </div>
        </Grid>
      </Container>
    </div>
  );
}

export default Nav;
