import { Link } from "react-router-dom";
import React, { useEffect } from "react";
import Container from "./Container";
import Grid from "./Grid";
import styles from "./Nav.module.css";
import classNames from "classnames";
import "../components/webMenu.css";

function Nav() {
  return (
    <div className={styles.nav}>
      <Container>
        <Grid className={styles.container}>
          <Link to="/">
            <div className={classNames(styles.logo, "title24x")}>JJUN Creative</div>
          </Link>
          <ul className={styles.menu}>
            <Link to="/brand">
              <li>Brand</li>
            </Link>
            <Link to="/foundations">
              <li>Foundations</li>
            </Link>
            <Link to="/components">
              <li>Components</li>
            </Link>
            <Link to="/contents">
              <li>Contents</li>
            </Link>
            <Link to="/resources">
              <li>Resources</li>
            </Link>
          </ul>

          <div className={"menuSize"}>
            <input onclick="toggleScrolling('#check_box')" type="checkbox" id="check_box" />
            <label for="check_box">
              <span></span>
              <span></span>
              <span></span>
            </label>
            <div id="side_menu">
              <ul>
                <Link to="/brand">
                  <li>Brand</li>
                </Link>
                <Link to="/foundations">
                  <li>Foundations</li>
                </Link>
                <Link to="/components">
                  <li>Components</li>
                </Link>
                <Link to="/contents">
                  <li>Contents</li>
                </Link>
                <Link to="/resources">
                  <li>Resources</li>
                </Link>
              </ul>
            </div>
          </div>
        </Grid>
      </Container>
    </div>
  );
  toggleScrolling = function (_selector) {
    let ele = document.querySelector(_selector);
    if (!ele) {
      return;
    }
    if (ele.style.overflow === "hidden") {
      ele.style.overflow = "";
      ele.style.height = "";
    } else {
      ele.style.overflow = "hidden";
      ele.style.height = "100%";
    }
  };
}

export default Nav;
