import { Link } from "react-router-dom";
import styles from "./FoundationsPage.module.css";
import classNames from "classnames";

function List() {
  return (
    <>
      <div className={styles.navigatorMain}>
        <div className={styles.navigatorWrapper}>
          <p className={classNames(styles.naviTitle, "bodyB20x")}>Foundations</p>
          <ul className={styles.navigator}>
            <li className={styles.menuWrapper} defaultChecked>
              <a className={classNames(styles.menu, "body16x")}>Colors</a>
            </li>
            <li className={styles.menuWrapper}>
              <a className={classNames(styles.menu, "body16x")}>Typorgaphy</a>
            </li>
            <li className={styles.menuWrapper}>
              <a className={classNames(styles.menu, "body16x")}>Icon</a>
            </li>
            <li className={styles.menuWrapper}>
              <a className={classNames(styles.menu, "body16x")}>Grid</a>
            </li>
            <li className={styles.menuWrapper}>
              <a className={classNames(styles.menu, "body16x")}>Radius</a>
            </li>
            <li className={styles.menuWrapper}>
              <a className={classNames(styles.menu, "body16x")}>Shadow</a>
            </li>
            <li className={styles.menuWrapper}>
              <a className={classNames(styles.menu, "body16x")}>Border</a>
            </li>
          </ul>
        </div>
      </div>
    </>
  );
}
const FilterCheckbox = ({ ...props }) => {
  // selectData {[key:string]: boolean}
  const [selectData, setSelectData] = useState({
    all: false,
    check: false,
  });
  // handleChange 에서 전체선택, 개별선택값에 따라 name에 맞는 checked 값을 변경한다.
  const handleChange = (e) => {
    const { checked, name } = e.target;

    if (name === "all") {
      setSelectData((prev) => {
        const newData = { ...prev };
        for (let key in newData) {
          newData[key] = checked;
        }
        console.log("마지막", newData);
        return newData;
      });
    } else {
      setSelectData({
        ...selectData,
        [name]: checked,
      });
    }
  };

  return (
    <div>
      <Checkbox onChange={handleChange} name={"all"} checked={selectData.all}>
        전체선택
      </Checkbox>
      <Checkbox onChange={handleChange} name={"check"} checked={selectData.check}>
        라벨이름
      </Checkbox>
    </div>
  );
};
export default List;
